import{_ as a,c as s,o as l,a4 as e}from"./chunks/framework.Ci4AqDqK.js";const u=JSON.parse('{"title":"linux(cenos) 安装 mysql","description":"","frontmatter":{},"headers":[],"relativePath":"articles/运维/linux(centos)安装mysql.md","filePath":"articles/运维/linux(centos)安装mysql.md"}'),n={name:"articles/运维/linux(centos)安装mysql.md"},c=e(`<h1 id="linux-cenos-安装-mysql" tabindex="-1">linux(cenos) 安装 mysql <a class="header-anchor" href="#linux-cenos-安装-mysql" aria-label="Permalink to &quot;linux(cenos) 安装 mysql&quot;">​</a></h1><h2 id="一、下载" tabindex="-1">一、下载 <a class="header-anchor" href="#一、下载" aria-label="Permalink to &quot;一、下载&quot;">​</a></h2><p><code>wget https://dev.mysql.com/get/Downloads/MySQL-5.7/mysql-5.7.44-linux-glibc2.12-x86_64.tar.gz</code> 注：也可本地下载再上传</p><h2 id="二、解压、移动、重命名" tabindex="-1">二、解压、移动、重命名 <a class="header-anchor" href="#二、解压、移动、重命名" aria-label="Permalink to &quot;二、解压、移动、重命名&quot;">​</a></h2><p><code>tar -xvf mysql-5.7.44-linux-glibc2.12-x86_64.tar.gz</code><br><code>mv mysql-5.7.44-linux-glibc2.12-x86_64 /usr/local/</code><br><code>mv mysql-5.7.44-linux-glibc2.12-x86_64/ mysql-5.7.44</code><br><code>ln -s mysql-5.7.44-linux-glibc2.12-x86_64/ mysql-5.7.44</code>（软链接[可选]）</p><h2 id="三、创建用户组、用户-数据目录赋予mysql用户权限" tabindex="-1">三、创建用户组、用户，数据目录赋予mysql用户权限 <a class="header-anchor" href="#三、创建用户组、用户-数据目录赋予mysql用户权限" aria-label="Permalink to &quot;三、创建用户组、用户，数据目录赋予mysql用户权限&quot;">​</a></h2><p><code>groupadd mysql</code><code>useradd -r -g mysql mysql</code><code>mkdir -p /usr/local/data/mysql</code><code>chown mysql:mysql -R /usr/local/data/mysql</code></p><h2 id="四、配置参数" tabindex="-1">四、配置参数 <a class="header-anchor" href="#四、配置参数" aria-label="Permalink to &quot;四、配置参数&quot;">​</a></h2><p>创建 /etc/my.cnf</p><div class="language-conf vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">conf</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>[mysqld]</span></span>
<span class="line"><span>nd-address=0.0.0.0</span></span>
<span class="line"><span>port=3306</span></span>
<span class="line"><span>user=mysql</span></span>
<span class="line"><span>basedir=/usr/local/mysql-5.7.44</span></span>
<span class="line"><span>datadir=/usr/local/data/mysql</span></span>
<span class="line"><span>socket=/usr/local/tmp/mysql.sock</span></span>
<span class="line"><span>log-error=/usr/local/data/mysql/mysql.err</span></span>
<span class="line"><span>pid-file=/usr/local/data/mysql/mysql.pid</span></span>
<span class="line"><span>#character config</span></span>
<span class="line"><span>character_set_server=utf8mb4</span></span>
<span class="line"><span>symbolic-links=0</span></span></code></pre></div><h2 id="五、初始化" tabindex="-1">五、初始化 <a class="header-anchor" href="#五、初始化" aria-label="Permalink to &quot;五、初始化&quot;">​</a></h2><p><code>/usr/local/mysql-5.7.44/bin/mysqld --defaults-file=/etc/my.cnf --basedir=/usr/local/mysql-5.7.44/ --datadir=/usr/local/data/mysql/ --user=mysql --initialize</code></p><h2 id="六、启动mysql" tabindex="-1">六、启动mysql <a class="header-anchor" href="#六、启动mysql" aria-label="Permalink to &quot;六、启动mysql&quot;">​</a></h2><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><p><a href="https://blog.51cto.com/u_16130732/6354410" target="_blank" rel="noreferrer">5 分钟搞定 Linux 安装 Mysql5.7</a><br><a href="https://cloud.tencent.com/developer/article/1451186" target="_blank" rel="noreferrer">Linux 安装 mysql5.7.44 --（傻瓜版 3 分钟搞定）</a></p>`,15),o=[c];function r(t,i,d,p,m,q){return l(),s("div",null,o)}const y=a(n,[["render",r]]);export{u as __pageData,y as default};
